const R = require('ramda');

const stringsArray = ['apple', 'banana', 'cherry', 'date'];

function addPrefix(prefix, str) {
    return prefix + str;
}

function prefixWithHello(str) {
    return addPrefix('Hello ', str);
}

const transformedArray = R.map(prefixWithHello, stringsArray);

console.log(transformedArray);

